import axios from "axios";
import React, { useContext, useState } from "react";
import { AuthContext } from "../usecontext/authcontext";


const SignIn=()=>{
    const [userlogin,setUserlogin]=useState(
        {
            username:undefined,
            password:undefined,
        }
    )
    const handleChange=()=>{
        setUserlogin((prev)=>(
            {
                ...prev,
                [e.target.id]:e.target.value
            }
        ))
    }
    const {user,loading,error,dispatch}=useContext(AuthContext)
    const handleClick=async (e)=>{
        e.preventDefault();
        dispatch({type:"login_start"})
        try{
            const res=await axios.post("auth/login",userlogin)
            dispatch({type:"login_success",payload:res.data})
        }catch(err){
            dispatch({type:"login_fail",payload:err.respone.data})
        }
    }
    return(
        <div className="sign_in_contain">
            <div className="username">
                <div className="username_text">
                    Username
                </div>
                <input classname="username_input"
                id="username"
                onChange={handleChange}/>
            </div>
            <div className="password">
                <div className="password_text">
                    Username
                </div>
                <input classname="password_input"
                id="password"
                onChange={handleChange}
                type="password"/>
            </div>
            <button className="login_btn"
            onClick={handleClick}  
            >Login</button>
        </div>
    )
}

export default SignIn;
